def main():
    with open("tokens.txt", "r") as f:  tokens = f.read().splitlines()
    for token in tokens:
        sex = requests.get("https://discord.com/api/v9/users/@me/guilds/premium/subscription-slots", headers={"accept": "*/*","accept-encoding": "gzip, deflate, br","accept-language": "en-US,en;q=0.9","authorization": token,"referer": "https://discord.com/channels/@me","sec-fetch-dest": "empty","sec-fetch-mode": "cors","sec-fetch-site": "same-origin","sec-gpc": "1","user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/102.0.5005.61 Safari/537.36","x-discord-locale": "en-US","x-super-properties": ""}) 
        if sex.status_code == 403:
            log(f"{colorama.Fore.RED}Token is locked.")   
